SELECT ORDERS.O_CUSTKEY, NATION.N_NAME, PARTSUPP.PS_PARTKEY, SUPPLIER.S_NATIONKEY, LINEITEM.L_COMMENT, MAX(LINEITEM.L_RETURNFLAG) FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY GROUP BY ORDERS.O_CUSTKEY, NATION.N_NAME, PARTSUPP.PS_PARTKEY, SUPPLIER.S_NATIONKEY, LINEITEM.L_COMMENT;
