SELECT NATION.N_REGIONKEY, SUPPLIER.S_ACCTBAL, CUSTOMER.C_COMMENT FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE SUPPLIER.S_PHONE > '25-843-787-7479' ORDER BY SUPPLIER.S_ACCTBAL DESC, CUSTOMER.C_COMMENT DESC, NATION.N_REGIONKEY ASC;
