SELECT ORDERS.O_COMMENT, LINEITEM.L_QUANTITY, PARTSUPP.PS_COMMENT, SUPPLIER.S_SUPPKEY FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE LINEITEM.L_SHIPDATE >= '1996-04-27' ORDER BY SUPPLIER.S_SUPPKEY DESC, ORDERS.O_COMMENT ASC, PARTSUPP.PS_COMMENT DESC;
