SELECT LINEITEM.L_COMMITDATE, ORDERS.O_ORDERDATE, CUSTOMER.C_ACCTBAL, COUNT(LINEITEM.L_SHIPMODE) FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY WHERE LINEITEM.L_DISCOUNT > 0.08 GROUP BY LINEITEM.L_COMMITDATE, ORDERS.O_ORDERDATE, CUSTOMER.C_ACCTBAL;
