SELECT LINEITEM.L_COMMITDATE, PART.P_CONTAINER, PARTSUPP.PS_AVAILQTY FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE LINEITEM.L_SUPPKEY = 121 ORDER BY PARTSUPP.PS_AVAILQTY ASC, LINEITEM.L_COMMITDATE ASC, PART.P_CONTAINER DESC;
