SELECT LINEITEM.L_ORDERKEY, CUSTOMER.C_COMMENT, PARTSUPP.PS_SUPPLYCOST, ORDERS.O_SHIPPRIORITY, SUPPLIER.S_NAME, NATION.N_COMMENT FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY ORDER BY SUPPLIER.S_NAME ASC;
