SELECT ORDERS.O_ORDERSTATUS, PARTSUPP.PS_SUPPLYCOST, LINEITEM.L_LINESTATUS, SUPPLIER.S_ADDRESS, CUSTOMER.C_NATIONKEY FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY;
