SELECT LINEITEM.L_SHIPDATE, PARTSUPP.PS_SUPPKEY, PART.P_RETAILPRICE, COUNT(PARTSUPP.PS_AVAILQTY), COUNT(LINEITEM.L_COMMENT) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY LINEITEM.L_SHIPDATE, PARTSUPP.PS_SUPPKEY, PART.P_RETAILPRICE;
