SELECT REGION.R_NAME, NATION.N_REGIONKEY, MAX(NATION.N_NATIONKEY) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY WHERE NATION.N_NAME != 'R THEODOLITES ACCORDING TO THE REQUESTS WAKE THINLY EXCUSES: PENDING REQUESTS HAGGLE FURIOUSL' AND NATION.N_REGIONKEY != 1992-09-03 GROUP BY REGION.R_NAME, NATION.N_REGIONKEY ORDER BY MAX(NATION.N_NATIONKEY) ASC;
