SELECT SUPPLIER.S_NAME, CUSTOMER.C_CUSTKEY, PART.P_NAME, PARTSUPP.PS_AVAILQTY, NATION.N_NATIONKEY FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY ORDER BY SUPPLIER.S_NAME DESC;
