SELECT LINEITEM.L_RETURNFLAG, ORDERS.O_TOTALPRICE, CUSTOMER.C_ADDRESS, COUNT(CUSTOMER.C_COMMENT) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY GROUP BY LINEITEM.L_RETURNFLAG, ORDERS.O_TOTALPRICE, CUSTOMER.C_ADDRESS ORDER BY COUNT(CUSTOMER.C_COMMENT) DESC;
