SELECT LINEITEM.L_EXTENDEDPRICE, SUPPLIER.S_ACCTBAL, PARTSUPP.PS_AVAILQTY, MAX(LINEITEM.L_COMMITDATE), MIN(LINEITEM.L_SHIPINSTRUCT), MAX(SUPPLIER.S_NAME) FROM SUPPLIER, PARTSUPP, LINEITEM WHERE SUPPLIER.S_ACCTBAL < 5302.37 GROUP BY LINEITEM.L_EXTENDEDPRICE, SUPPLIER.S_ACCTBAL, PARTSUPP.PS_AVAILQTY ORDER BY MAX(SUPPLIER.S_NAME) ASC, MIN(LINEITEM.L_SHIPINSTRUCT) ASC, MAX(LINEITEM.L_COMMITDATE) ASC;
