SELECT LINEITEM.L_SHIPINSTRUCT, CUSTOMER.C_CUSTKEY, ORDERS.O_COMMENT, MIN(ORDERS.O_ORDERSTATUS), MAX(CUSTOMER.C_ACCTBAL) FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY WHERE CUSTOMER.C_NATIONKEY < 21 GROUP BY LINEITEM.L_SHIPINSTRUCT, CUSTOMER.C_CUSTKEY, ORDERS.O_COMMENT ORDER BY MAX(CUSTOMER.C_ACCTBAL) DESC, MIN(ORDERS.O_ORDERSTATUS) DESC;
