SELECT REGION.R_NAME, PARTSUPP.PS_SUPPKEY, SUPPLIER.S_NAME, LINEITEM.L_SHIPMODE, NATION.N_COMMENT, COUNT(LINEITEM.L_LINESTATUS) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY GROUP BY REGION.R_NAME, PARTSUPP.PS_SUPPKEY, SUPPLIER.S_NAME, LINEITEM.L_SHIPMODE, NATION.N_COMMENT ORDER BY COUNT(LINEITEM.L_LINESTATUS) DESC;
