SELECT LINEITEM.L_COMMENT, CUSTOMER.C_MKTSEGMENT, PARTSUPP.PS_PARTKEY, PART.P_MFGR, ORDERS.O_ORDERDATE FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY;
