SELECT SUPPLIER.S_SUPPKEY, LINEITEM.L_QUANTITY, ORDERS.O_TOTALPRICE, PARTSUPP.PS_SUPPLYCOST, CUSTOMER.C_PHONE, NATION.N_REGIONKEY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE PARTSUPP.PS_AVAILQTY != 2957 ORDER BY SUPPLIER.S_SUPPKEY DESC, NATION.N_REGIONKEY DESC, CUSTOMER.C_PHONE ASC, ORDERS.O_TOTALPRICE DESC, PARTSUPP.PS_SUPPLYCOST DESC;
