SELECT PARTSUPP.PS_SUPPLYCOST, SUPPLIER.S_SUPPKEY, NATION.N_NAME FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY WHERE SUPPLIER.S_ADDRESS <= '9SQ4BBH2FQEMAFOOCY45SRTXO6YUOG' AND PARTSUPP.PS_AVAILQTY <= 4410 ORDER BY SUPPLIER.S_SUPPKEY DESC, PARTSUPP.PS_SUPPLYCOST DESC, NATION.N_NAME DESC;
