SELECT LINEITEM.L_SHIPMODE, PART.P_TYPE, CUSTOMER.C_MKTSEGMENT, ORDERS.O_ORDERPRIORITY, PARTSUPP.PS_SUPPKEY, MAX(PART.P_BRAND) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY GROUP BY LINEITEM.L_SHIPMODE, PART.P_TYPE, CUSTOMER.C_MKTSEGMENT, ORDERS.O_ORDERPRIORITY, PARTSUPP.PS_SUPPKEY ORDER BY PART.P_TYPE ASC, LINEITEM.L_SHIPMODE ASC, ORDERS.O_ORDERPRIORITY ASC, CUSTOMER.C_MKTSEGMENT ASC, PARTSUPP.PS_SUPPKEY ASC;
