SELECT LINEITEM.L_COMMENT, ORDERS.O_ORDERSTATUS, CUSTOMER.C_COMMENT, SUM(ORDERS.O_TOTALPRICE) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY WHERE CUSTOMER.C_ADDRESS >= 'TV NCR2YKUPGN73MQUDO' AND LINEITEM.L_TAX >= 0.03 GROUP BY LINEITEM.L_COMMENT, ORDERS.O_ORDERSTATUS, CUSTOMER.C_COMMENT;
