SELECT SUPPLIER.S_COMMENT, CUSTOMER.C_ACCTBAL, NATION.N_REGIONKEY, ORDERS.O_COMMENT FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE CUSTOMER.C_PHONE < '28-328-564-7630';
