SELECT LINEITEM.L_COMMENT, PARTSUPP.PS_COMMENT, PART.P_PARTKEY, MIN(PART.P_NAME) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY LINEITEM.L_COMMENT, PARTSUPP.PS_COMMENT, PART.P_PARTKEY ORDER BY PART.P_PARTKEY DESC, PARTSUPP.PS_COMMENT DESC;
