SELECT ORDERS.O_CLERK, LINEITEM.L_PARTKEY, PARTSUPP.PS_SUPPLYCOST, CUSTOMER.C_ACCTBAL, MIN(ORDERS.O_ORDERPRIORITY) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY WHERE ORDERS.O_ORDERDATE < '1993-05-14' GROUP BY ORDERS.O_CLERK, LINEITEM.L_PARTKEY, PARTSUPP.PS_SUPPLYCOST, CUSTOMER.C_ACCTBAL ORDER BY PARTSUPP.PS_SUPPLYCOST ASC, ORDERS.O_CLERK ASC, CUSTOMER.C_ACCTBAL DESC, LINEITEM.L_PARTKEY DESC;
