SELECT LINEITEM.L_PARTKEY, ORDERS.O_CLERK, SUPPLIER.S_ADDRESS, PARTSUPP.PS_COMMENT, MAX(LINEITEM.L_TAX) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY GROUP BY LINEITEM.L_PARTKEY, ORDERS.O_CLERK, SUPPLIER.S_ADDRESS, PARTSUPP.PS_COMMENT;
