SELECT ORDERS.O_TOTALPRICE, PARTSUPP.PS_PARTKEY, LINEITEM.L_COMMITDATE, CUSTOMER.C_MKTSEGMENT, MAX(ORDERS.O_TOTALPRICE) FROM CUSTOMER, ORDERS, LINEITEM, PARTSUPP GROUP BY ORDERS.O_TOTALPRICE, PARTSUPP.PS_PARTKEY, LINEITEM.L_COMMITDATE, CUSTOMER.C_MKTSEGMENT HAVING MAX(ORDERS.O_TOTALPRICE) >= 24691.53 ORDER BY MAX(ORDERS.O_TOTALPRICE) DESC;
