SELECT CUSTOMER.C_MKTSEGMENT, NATION.N_NAME, REGION.R_COMMENT, MAX(REGION.R_NAME), MAX(NATION.N_NATIONKEY), MIN(CUSTOMER.C_CUSTKEY) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY WHERE CUSTOMER.C_NATIONKEY = 3 GROUP BY CUSTOMER.C_MKTSEGMENT, NATION.N_NAME, REGION.R_COMMENT HAVING MIN(CUSTOMER.C_CUSTKEY) < 67 ORDER BY NATION.N_NAME ASC;
