SELECT LINEITEM.L_RETURNFLAG, ORDERS.O_TOTALPRICE, CUSTOMER.C_PHONE, PARTSUPP.PS_COMMENT, MIN(ORDERS.O_ORDERSTATUS) FROM CUSTOMER, ORDERS, LINEITEM, PARTSUPP GROUP BY LINEITEM.L_RETURNFLAG, ORDERS.O_TOTALPRICE, CUSTOMER.C_PHONE, PARTSUPP.PS_COMMENT HAVING MIN(ORDERS.O_ORDERSTATUS) <= 'O' ORDER BY ORDERS.O_TOTALPRICE ASC;
