SELECT LINEITEM.L_COMMENT, ORDERS.O_ORDERKEY, PARTSUPP.PS_SUPPLYCOST FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY WHERE PARTSUPP.PS_COMMENT > 'LY CAREFULLY SPECIAL ASYMPTOTES. FURIOUSLY PENDING INSTRUCTIONS HAGGLE BLITHELY BRAVELY PENDING REQUESTS. CAREFULLY F' AND LINEITEM.L_COMMITDATE > '1993-05-07';
