SELECT PARTSUPP.PS_AVAILQTY, ORDERS.O_ORDERSTATUS, LINEITEM.L_TAX, SUPPLIER.S_NAME FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY ORDER BY SUPPLIER.S_NAME ASC, ORDERS.O_ORDERSTATUS ASC, LINEITEM.L_TAX ASC, PARTSUPP.PS_AVAILQTY DESC;
