SELECT LINEITEM.L_RECEIPTDATE, PART.P_MFGR, PARTSUPP.PS_SUPPLYCOST FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE PART.P_TYPE < 'MEDIUM POLISHED NICKEL' ORDER BY LINEITEM.L_RECEIPTDATE DESC, PARTSUPP.PS_SUPPLYCOST ASC, PART.P_MFGR DESC;
