SELECT LINEITEM.L_PARTKEY, CUSTOMER.C_NATIONKEY, ORDERS.O_ORDERSTATUS, MIN(ORDERS.O_ORDERPRIORITY), MIN(ORDERS.O_ORDERSTATUS) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY GROUP BY LINEITEM.L_PARTKEY, CUSTOMER.C_NATIONKEY, ORDERS.O_ORDERSTATUS ORDER BY MIN(ORDERS.O_ORDERPRIORITY) ASC;
