SELECT LINEITEM.L_SHIPMODE, PARTSUPP.PS_SUPPLYCOST, SUPPLIER.S_PHONE, NATION.N_NATIONKEY, SUM(LINEITEM.L_LINENUMBER) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY GROUP BY LINEITEM.L_SHIPMODE, PARTSUPP.PS_SUPPLYCOST, SUPPLIER.S_PHONE, NATION.N_NATIONKEY HAVING SUM(LINEITEM.L_LINENUMBER) > 3;
