SELECT PARTSUPP.PS_AVAILQTY, SUPPLIER.S_PHONE, NATION.N_COMMENT, MIN(NATION.N_NAME) FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY GROUP BY PARTSUPP.PS_AVAILQTY, SUPPLIER.S_PHONE, NATION.N_COMMENT HAVING MIN(NATION.N_NAME) >= 'ORDERS' ORDER BY MIN(NATION.N_NAME) DESC;
