SELECT PART.P_CONTAINER, NATION.N_REGIONKEY, PARTSUPP.PS_COMMENT, SUPPLIER.S_ADDRESS FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE PARTSUPP.PS_SUPPKEY <= 880 AND PARTSUPP.PS_SUPPKEY < 650 AND PARTSUPP.PS_PARTKEY <= 136 ORDER BY PARTSUPP.PS_COMMENT ASC;
