SELECT SUPPLIER.S_NATIONKEY, PART.P_RETAILPRICE, PARTSUPP.PS_AVAILQTY, COUNT(PARTSUPP.PS_PARTKEY) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY SUPPLIER.S_NATIONKEY, PART.P_RETAILPRICE, PARTSUPP.PS_AVAILQTY HAVING COUNT(PARTSUPP.PS_PARTKEY) >= 3;
