SELECT SUPPLIER.S_NATIONKEY, CUSTOMER.C_ACCTBAL, ORDERS.O_TOTALPRICE, NATION.N_NAME, COUNT(ORDERS.O_TOTALPRICE) FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE CUSTOMER.C_NATIONKEY != 21 GROUP BY SUPPLIER.S_NATIONKEY, CUSTOMER.C_ACCTBAL, ORDERS.O_TOTALPRICE, NATION.N_NAME;
